import { useState, useEffect } from 'react';

export default function Price() {
   const [products, setProducts] = useState([]);

   useEffect(() => {
      fetch('http://localhost:3000/products')
         .then((r) => r.json())
         .then((data) =>
            setProducts(
               data.sort(
                  (a, b) =>
                     a.price.substring(1).replace(',', '') -
                     b.price.substring(1).replace(',', '')
               )
            )
         );
   }, []);

   function handleHeart(id, reverse) {
      fetch(`http://localhost:3000/products/${id}`, {
         method: 'PATCH',
         headers: {
            'Content-Type': 'application/json',
         },
         body: JSON.stringify({ heart: reverse }),
      })
         .then((r) => r.json())
         .then((data) => setProducts(data.products.id, data.products.heart));
   }

   return products.map((product) => (
      <div key={product.id}>
         <img src={product.image} alt={product.title + product.model} />
         <h1>{product.title}</h1>
         <p>{product.model}</p>
         <p>{product.details}</p>
         <p>{product.price}</p>
         <p
            style={{ cursor: 'pointer' }}
            onClick={() => handleHeart(product.id, product.heart)}
         >
            {product.heart === false ? '♡' : '♥'}
         </p>
      </div>
   ));
}
