import React, { useState, useEffect } from 'react';
import { NavLink, useNavigate, Outlet } from 'react-router-dom';
import applecore from './media/Apple Core.png';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Navbar, Container } from 'react-bootstrap';

export default function App() {
   const [search, setSearch] = useState('');
   const navigate = useNavigate();
   useEffect(() => {
      if (window.location.pathname === '/') {
         navigate('/routes/AppleCore');
      }
   }, []);

   return (
      <div>
         <Navbar bg="white" expand="lg">
            <Container>
               <NavLink to="/routes/AppleCore">
                  <img src={applecore} width={20} alt={'apple core logo'} />
               </NavLink>
               <NavLink
                  style={({ isActive }) =>
                     isActive
                        ? {
                             color: '#fff',
                             textDecoration: 'none',
                          }
                        : {
                             color: '#545e6f',
                             textDecoration: 'none',
                          }
                  }
                  to="/routes/Mac"
               >
                  Mac
               </NavLink>
               <NavLink to="/routes/IMac">iMac</NavLink>
               <NavLink to="/routes/MacBook">MacBook</NavLink>
               <NavLink to="/routes/IPad">iPad</NavLink>
               <NavLink to="/routes/IPhone">iPhone</NavLink>
               <NavLink to="/routes/Watch">Watch</NavLink>
               <NavLink to="/routes/AirPods">AirPods</NavLink>
               <NavLink to="/routes/TVandHome">TV & Home</NavLink>
               <NavLink to="/routes/Signup">Sign Up</NavLink>
               <NavLink to="/routes/Signin">Sign In</NavLink>
               <input
                  type="text"
                  placeholder="Search..."
                  value={search}
                  onChange={(e) => setSearch(e.target.value)}
               />
               <NavLink to="/routes/Price">$</NavLink>
               <NavLink to="/routes/Hearts">â™¥</NavLink>
               <button
                  style={{
                     border: 'none',
                     background: 'none',
                     cursor: 'pointer',
                  }}
                  onClick={() => alert('Dark Mode')}
               >
                  ðŸ’¡
               </button>
            </Container>
         </Navbar>
         <Outlet />
      </div>
   );
}
