export default function Watch({ products, handleHeart }) {
   return products
      .filter((product) => product.title.includes('Watch'))
      .map((product) => (
         <div key={product.id}>
            <img src={product.image} alt={product.title + product.model} />
            <h1>{product.title}</h1>
            <p>{product.model}</p>
            <p>{product.details}</p>
            <p>{product.price}</p>
            <button onClick={() => handleHeart(product.id, !product.heart)}>
               {product.heart === false ? '♡' : '♥'}
            </button>
         </div>
      ));
}
