               style={({ isActive }) => ({
                  color: isActive ? 'red' : '',
               })}
               to="/routes/IMac"
            >
               iMac
            </NavLink>
            &nbsp;&nbsp;
            <NavLink
               style={({ isActive }) => ({
                  color: isActive ? 'red' : '',
               })}
               to="/routes/MacBook"
            >
               MacBook
            </NavLink>
            &nbsp;&nbsp;
