               style={({ isActive }) => ({
                  color: isActive ? 'red' : '',
               })}
               to="/routes/IPad"
            >
               iPad
            </NavLink>
            &nbsp;&nbsp;
            <NavLink
               style={({ isActive }) => ({
                  color: isActive ? 'red' : '',
               })}
               to="/routes/IPhone"
            >
               iPhone
            </NavLink>
            &nbsp;&nbsp;
            <NavLink
               style={({ isActive }) => ({
                  color: isActive ? 'red' : '',
               })}
               to="/routes/Watch"
            >
               Watch
            </NavLink>
            &nbsp;&nbsp;
            <NavLink
               style={({ isActive }) => ({
                  color: isActive ? 'red' : '',
               })}
               to="/routes/AirPods"
            >
               AirPods
            </NavLink>
            &nbsp;&nbsp;
            <NavLink
               style={({ isActive }) => ({
                  color: isActive ? 'red' : '',
               })}
               to="/routes/TVandHome"
            >
               TV & Home
            </NavLink>
            &nbsp;&nbsp;
            <NavLink
               style={({ isActive }) => ({
                  color: isActive ? 'red' : '',
               })}
               to="/routes/Signup"
            >
               Sign Up
            </NavLink>
            &nbsp;&nbsp;
            <NavLink
               style={({ isActive }) => ({
                  color: isActive ? 'red' : '',
               })}
               to="/routes/Signin"
            >
               Sign In
            </NavLink>
            &nbsp;&nbsp;
            <input type="text" />
            &nbsp;&nbsp;
            <NavLink
               style={({ isActive }) => ({
                  color: isActive ? 'red' : '',
               })}
               to="/routes/Price"
            >
               $
