import { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Container, Card, Col, Row, Button } from 'react-bootstrap';

export default function AppleCore() {
   const [products, setProducts] = useState([]);
   const [rerender, setRerender] = useState(false);

   useEffect(() => {
      fetch('http://localhost:3000/products')
         .then((r) => r.json())
         .then((data) => setProducts(data.sort((a, b) => a.title < b.title)));
   }, [rerender]);

   return (
      <>
         <Container fluid align="center">
            <Row sm={1} md={2} lg={3}>
               {products.map((product) => (
                  <Col key={product.id}>
                     <Card style={{ width: '18rem', padding: '10px' }}>
                        <Card.Img
                           src={product.image}
                           alt={product.title + product.model}
                        />
                        <br />
                        <Card.Title>{product.title}</Card.Title>
                        <Card.Text>{product.model}</Card.Text>
                        <Card.Text>{product.price}</Card.Text>
                        <Card.Text
                           style={{ cursor: 'pointer' }}
                           onClick={() =>
                              fetch(
                                 `http://localhost:3000/products/${product.id}`,
                                 {
                                    method: 'PATCH',
                                    headers: {
                                       'Content-Type': 'application/json',
                                    },
                                    body: JSON.stringify({
                                       heart: !product.heart,
                                    }),
                                 }
                              ).then(setRerender(!rerender))
                           }
                        >
                           {product.heart === false ? '♡' : '♥'}
                        </Card.Text>
                     </Card>
                     <br />
                  </Col>
               ))}
            </Row>
         </Container>
         <Navbar bg="dark">
            <Container>
               <NavLink
                  style={{
                     color: 'gray',
                     textDecoration: 'none',
                     textAlign: 'center',
                     marginLeft: 'auto',
                     marginRight: 'auto',
                  }}
                  to="/routes/AppleCore"
               >
                  <img src={applecore} width={20} alt={'apple core logo'} />
                  <br />
                  <small>
                     Copyright © 2022 Apple Core Inc. All rights reserved.
                  </small>
               </NavLink>
            </Container>
         </Navbar>
      </>
   );
}
