import React, { useState, useEffect } from 'react';
import { NavLink, useNavigate, Outlet } from 'react-router-dom';
import applecore from './media/Apple Core.png';
import AppleCore from './routes/AppleCore';

export default function App() {
   const [search, setSearch] = useState('');
   const navigate = useNavigate();
   useEffect(() => {
      if (window.location.pathname === '/') {
         navigate('/routes/AppleCore');
      }
   }, []);

   return (
      <div>
         <nav>
            <NavLink to="/routes/AppleCore">
               <img src={applecore} width={20} alt={'apple core logo'} />
            </NavLink>
            &nbsp;&nbsp;
            <NavLink
               style={({ isActive }) => ({
                  color: isActive ? 'red' : '',
               })}
               to="/routes/Mac"
            >
               Mac
            </NavLink>
            &nbsp;&nbsp;
            <NavLink
               style={({ isActive }) => ({
                  color: isActive ? 'red' : '',
               })}
               to="/routes/IMac"
            >
               iMac
            </NavLink>
            &nbsp;&nbsp;
            <NavLink
               style={({ isActive }) => ({
                  color: isActive ? 'red' : '',
               })}
               to="/routes/MacBook"
            >
               MacBook
            </NavLink>
            &nbsp;&nbsp;
            <NavLink
               style={({ isActive }) => ({
                  color: isActive ? 'red' : '',
               })}
               to="/routes/IPad"
            >
               iPad
            </NavLink>
            &nbsp;&nbsp;
            <NavLink
               style={({ isActive }) => ({
                  color: isActive ? 'red' : '',
               })}
               to="/routes/IPhone"
            >
               iPhone
            </NavLink>
            &nbsp;&nbsp;
            <NavLink
               style={({ isActive }) => ({
                  color: isActive ? 'red' : '',
               })}
               to="/routes/Watch"
            >
               Watch
            </NavLink>
            &nbsp;&nbsp;
            <NavLink
               style={({ isActive }) => ({
                  color: isActive ? 'red' : '',
               })}
               to="/routes/AirPods"
            >
               AirPods
            </NavLink>
            &nbsp;&nbsp;
            <NavLink
               style={({ isActive }) => ({
                  color: isActive ? 'red' : '',
               })}
               to="/routes/TVandHome"
            >
               TV & Home
            </NavLink>
            &nbsp;&nbsp;
            <NavLink
               style={({ isActive }) => ({
                  color: isActive ? 'red' : '',
               })}
               to="/routes/Signup"
            >
               Sign Up
            </NavLink>
            &nbsp;&nbsp;
            <NavLink
               style={({ isActive }) => ({
                  color: isActive ? 'red' : '',
               })}
               to="/routes/Signin"
            >
               Sign In
            </NavLink>
            &nbsp;&nbsp;
            <input
               type="text"
               placeholder="Search..."
               onChange={(e) => setSearch(e.target.value)}
            />
            &nbsp;&nbsp;
            <NavLink
               style={({ isActive }) => ({
                  color: isActive ? 'red' : '',
               })}
               to="/routes/Price"
            >
               $
            </NavLink>
            &nbsp;&nbsp;
            <NavLink
               style={({ isActive }) => ({
                  color: isActive ? 'red' : '',
               })}
               to="/routes/Hearts"
            >
               ♥
            </NavLink>
            <button
               style={{ border: 'none', background: 'none', cursor: 'pointer' }}
               onClick={() => alert('Dark Mode')}
            >
               💡
            </button>
         </nav>
         <Outlet />
         <AppleCore search={search} setSearch={setSearch} />
      </div>
   );
}
