import { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { Container, Card, Col, Row, Button } from 'react-bootstrap';
import amazonbutton from '../media/amazonbutton.png';

export default function Cart() {
   const [products, setProducts] = useState([]);
   const [rerender, setRerender] = useState(false);
   const navigate = useNavigate();

   useEffect(() => {
      fetch('http://localhost:3000/products')
         .then((r) => r.json())
         .then((data) => setProducts(data));
   }, [rerender]);

   function handleHeart(product) {
      fetch(`http://localhost:3000/products/${product.id}`, {
         method: 'PATCH',
         headers: {
            'Content-Type': 'application/json',
         },
         body: JSON.stringify({
            heart: !product.heart,
         }),
      }).then(setRerender(!rerender));
   }

   function handleAddToCart(product) {
      fetch(`http://localhost:3000/products/${product.id}`, {
         method: 'PATCH',
         headers: {
            'Content-Type': 'application/json',
         },
         body: JSON.stringify({
            inCart: !product.inCart,
         }),
      }).then(setRerender(!rerender));
   }

   return (
      <Container fluid align="center">
         <p className="display-6 text-center">Cart</p>
         <Row sm={1} md={2} lg={3}>
            {products
               .filter((product) => product.inCart === true)
               .map((product) => (
                  <Col key={product.id}>
                     <Card style={{ width: '18rem', padding: '10px' }}>
                        <Card.Img
                           src={product.image}
                           alt={product.title + product.model}
                        />
                        <br />
                        <Container className="d-flex justify-content-between">
                           <Card.Title>{product.title}</Card.Title>
                           <Card.Text>{product.model}</Card.Text>
                           <Card.Text
                              style={{ cursor: 'pointer' }}
                              onClick={() => handleHeart(product)}
                           >
                              {product.heart ? '♥' : '♡'}
                           </Card.Text>
                        </Container>
                        <Container className="d-flex justify-content-between">
                           <Card.Text>{product.price}</Card.Text>
                           <Button
                              variant="outline-secondary"
                              onClick={() => handleAddToCart(product)}
                           >
                              {product.inCart ? 'Remove' : 'Add to Cart'}
                           </Button>
                        </Container>
                        <Container>
                           <p></p>
                           <a
                              href={`https://www.amazon.com/s?k=${product.title}`}
                           >
                              <img
                                 src={amazonbutton}
                                 alt="amazon button"
                                 width="66%"
                              />
                           </a>
                           <br />
                        </Container>
                     </Card>
                     <br />
                  </Col>
               ))}
         </Row>
      </Container>
   );
}
