import React, { useContext } from 'react';
import { StateContext } from '../context';
import { useNavigate } from 'react-router-dom';
import { Container } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

export default function SignedIn() {
   const { currentUser, setCurrentUser, mode } = useContext(StateContext);
   const navigate = useNavigate();

   function reset() {
      setCurrentUser('');
      navigate('/routes/AppleCore');
   }

   return (
      <div
         className={
            mode === 'light'
               ? 'jumbotron jumbotron-fluid bg-white text-dark p-1'
               : 'jumbotron jumbotron-fluid bg-black text-light p-1'
         }
         id="signupForm"
      >
         <br />
         <p className="display-6 text-center">
            You have successfully signed in!
         </p>
         <br />
         <h2 className=" text-center">Welcome back, {currentUser}!</h2>
         <br />
         <br />
         <br />
         <br />
         <br />
         <br />
         <br />
      </div>
   );
}
