import { useState, useEffect } from 'react';

export default function Hearts() {
   const [products, setProducts] = useState([]);

   useEffect(() => {
      fetch('http://localhost:3000/products')
         .then((r) => r.json())
         .then((data) => setProducts(data));
   }, []);

   function handleHeart(id, heart) {
      fetch(`http://localhost:3000/products/${id}`, {
         method: 'PATCH',
         headers: {
            'Content-Type': 'application/json',
         },
         body: JSON.stringify({ heart: !heart }),
      })
         .then((r) => r.json())
         .then((data) => setProducts(data.products.id, data.products.heart));
   }

   return products
      .filter((product) => product.heart === true)
      .map((product) => (
         <div key={product.id}>
            <img src={product.image} alt={product.title + product.model} />
            <h1>{product.title}</h1>
            <p>{product.model}</p>
            <p>{product.details}</p>
            <p>{product.price}</p>
            <p
               style={{ cursor: 'pointer' }}
               onClick={() => handleHeart(product.id, product.heart)}
            >
               {product.heart === false ? '♡' : '♥'}
            </p>
         </div>
      ));
}
