import React from 'react';
import { NavLink, Outlet } from 'react-router-dom';
import applecore from './media/Apple Core.png';

export default function App() {
   function handleHeart() {
      fetch(`http://localhost:3000/products/${id}`, {
         method: 'PATCH',
         headers: {
            'Content-Type': 'application/json',
         },
         body: JSON.stringify({ todo: { completed: completed } }),
      })
         .then((r) => r.json())
         .then((data) => onUpdateTodo(data.todo.id, data.todo.completed));
      // then use onUpdateTodo to update todo in state}
   }

   return (
      <div>
         <nav>
            <NavLink to="/routes/AppleCore">
               <img src={applecore} width={20} alt={'apple core logo'} />
            </NavLink>
            &nbsp;&nbsp;
            <NavLink
               style={({ isActive }) => ({
                  color: isActive ? 'red' : '',
               })}
               to="/routes/Mac"
            >
               Mac
            </NavLink>
            &nbsp;&nbsp;
            <NavLink
               style={({ isActive }) => ({
                  color: isActive ? 'red' : '',
               })}
               to="/routes/IMac"
            >
               iMac
            </NavLink>
            &nbsp;&nbsp;
            <NavLink
               style={({ isActive }) => ({
                  color: isActive ? 'red' : '',
               })}
               to="/routes/MacBook"
            >
               MacBook
            </NavLink>
            &nbsp;&nbsp;
            <NavLink
               style={({ isActive }) => ({
                  color: isActive ? 'red' : '',
               })}
               to="/routes/IPad"
            >
               iPad
            </NavLink>
            &nbsp;&nbsp;
            <NavLink
               style={({ isActive }) => ({
                  color: isActive ? 'red' : '',
               })}
               to="/routes/IPhone"
            >
               iPhone
            </NavLink>
            &nbsp;&nbsp;
            <NavLink
               style={({ isActive }) => ({
                  color: isActive ? 'red' : '',
               })}
               to="/routes/Watch"
            >
               Watch
            </NavLink>
            &nbsp;&nbsp;
            <NavLink
               style={({ isActive }) => ({
                  color: isActive ? 'red' : '',
               })}
               to="/routes/AirPods"
            >
               AirPods
            </NavLink>
            &nbsp;&nbsp;
            <NavLink
               style={({ isActive }) => ({
                  color: isActive ? 'red' : '',
               })}
               to="/routes/TVandHome"
            >
               TV & Home
            </NavLink>
            &nbsp;&nbsp;
            <NavLink
               style={({ isActive }) => ({
                  color: isActive ? 'red' : '',
               })}
               to="/routes/Signup"
            >
               Sign Up
            </NavLink>
            &nbsp;&nbsp;
            <NavLink
               style={({ isActive }) => ({
                  color: isActive ? 'red' : '',
               })}
               to="/routes/Signin"
            >
               Sign In
            </NavLink>
            &nbsp;&nbsp;
            <input type="text" />
            &nbsp;&nbsp;
            <NavLink
               style={({ isActive }) => ({
                  color: isActive ? 'red' : '',
               })}
               to="/routes/Price"
            >
               $
            </NavLink>
            &nbsp;&nbsp;
            <NavLink
               style={({ isActive }) => ({
                  color: isActive ? 'red' : '',
               })}
               to="/routes/Hearts"
            >
               ♥
            </NavLink>
            &nbsp;&nbsp;
            <button href={''} onClick={() => alert('Dark Mode')}>
               💡
            </button>
         </nav>
         <Outlet />
      </div>
   );
}
